import React, { useEffect, useState } from 'react'

const Display = () => {

    const [item, setItem] = useState([]);

    const fetchApi = async () => {

        const response = await fetch("https://assessment.api.vweb.app/products");
        setItem(await response.json());
        console.log(item)
    }

    useEffect(() => {
        fetchApi();
    },[setItem]);

    const handleDelete = (id) =>{
        const update = item.filter((curitem,ind) =>{
            return ind!==id

        })
        setItem(update)
    }

    return (
        <>
            <div className="main-container">
                {
                    item.map((curItem) => {
                        const { product_id, selling_price, name, stock } = curItem
                        return (

                            <>
                                <div className="container">


                                    <div className="name_sec" >
                                        <h1>{name}</h1>
                                    </div>


                                    <div className="price_sec">
                                        <h2>Price : {selling_price}</h2>
                                    </div>
                                    <div className="stock_sec">
                                        <h2>Stock : {stock}</h2>
                                    </div>
                                    <div className="remove-item">
                                        <i className="fas fa-trash" onClick={()=>handleDelete(product_id)}></i>
                                    </div>

                                    <div className="clear-all">

                                   <button className="btn">Clear all</button>
                                    </div>
                                </div>

                            </>
                        )
                    })
                };
            </div>

        </>
    )
}

export default Display
